import SwiftUI
import UniformTypeIdentifiers
#if canImport(AppKit)
import AppKit
#else
import UIKit
#endif

struct ShareMergeRequestIcon: View {
    let request: UniversalMergeRequest
    @State private var chosenEmoji: String = ""
    @State private var isVisible: Bool = false

    var body: some View {
        ZStack {
            Button {
                copyToPasteboard()
            } label: {
                HStack {
                    Text("Share")
                        .transition(
                            .opacity
                                .combined(with: .scale(0, anchor: .leading)
                                    .animation(.snappy.delay(0.1))
                                    .combined(with: .blurReplace)
                                )
                                .animation(.snappy)
                        )

                }
                .foregroundStyle(.primary)
                .padding(.leading, 8)
                .padding(.trailing, 8)
                .padding(.vertical, 2)
                .background(
                    RoundedRectangle(cornerRadius: 20)
                        .fill(Color.primary.gradient.quinary)
                )
            }
            .font(.system(size: 11))
            .padding(.horizontal, 6)
            .buttonStyle(.plain)
//            .foregroundStyle(.primary)


            Text(chosenEmoji)
                .font(.system(size: 36))
                .opacity(isVisible ? 1 : 0)
                .offset(y: isVisible ? -40 : 0)
                .scaleEffect(x: isVisible ? 1 : 0, y: isVisible ? 1 : 0)
                .allowsHitTesting(false)
        }
        .frame(height: 20)
        .onChange(of: chosenEmoji) { _, _ in
            let animation: Animation = .interpolatingSpring(stiffness: 130, damping: 12)
            withAnimation(animation) {
                isVisible = true
            }

            DispatchQueue.main.asyncAfter(deadline: .now() + .milliseconds(400), execute: {
                withAnimation(animation) {
                    isVisible = false
                }
            })
        }
    }

    func copyToPasteboard() {
        let emoji = String.FriendlyEmojis.randomElement() ?? "ğŸ¦†"
        let content = """
\(request.title ?? "") \(emoji)
\(request.url?.absoluteString ?? "")
"""
#if canImport(AppKit)
        let pasteboard = NSPasteboard.general
        pasteboard.clearContents()
        pasteboard.setString(content, forType: .string)
        pasteboard.setString(content, forType: .rtf)
#else
        let pasteboard = UIPasteboard.general
        pasteboard.string = content
#endif
        chosenEmoji = emoji
    }
}

#Preview {
    ShareMergeRequestIcon(request: .preview)
        .scenePadding()
}

